static f32 distace_angle_adjust(f32 i_distAdjust, s16 i_angle, f32 i_ratio) {
    f32 tmp = i_angle / (f32)0x8000;
    if (tmp < 0.0f) {
        tmp = -tmp;
    }

    return i_distAdjust * ((1.0f - i_ratio) + (i_ratio * ((1.0f - tmp) * (1.0f - tmp))));
}

