void dName_c::MojiSelect() {
    if (mDoCPd_c::getTrigA(PAD_1)) {
        selectMojiSet();
    } else if (stick->checkRightTrigger()) {
        mDoAud_seStart(Z2SE_SY_NAME_CURSOR, 0, 0, 0);
        mPrevColumn = mCharColumn;
        mPrevRow = mCharRow;
        mCharColumn++;

        if (mCharColumn > 12) {
            mCharColumn = 0;
        }
        MojiSelectAnmInit();
        mSelProc = PROC_MOJI_SEL_ANM;
    } else if (stick->checkLeftTrigger()) {
        mDoAud_seStart(Z2SE_SY_NAME_CURSOR, 0, 0, 0);
        mPrevColumn = mCharColumn;
        mPrevRow = mCharRow;

        if (mCharColumn == 0) {
            mCharColumn = 12;
        } else {
            mCharColumn--;
        }
        MojiSelectAnmInit();
        mSelProc = PROC_MOJI_SEL_ANM;
    } else if (stick->checkUpTrigger()) {
        mPrevColumn = mCharColumn;
        mPrevRow = mCharRow;
        MojiSelectAnmInit();

        if (mCharRow == 0) {
            mDoAud_seStart(Z2SE_SY_CURSOR_OPTION, 0, 0, 0);
            menuCursorPosSet();
            mSelProc = PROC_MOJI_SEL_ANM2;
        } else {
            mDoAud_seStart(Z2SE_SY_NAME_CURSOR, 0, 0, 0);
            mCharRow--;
            mSelProc = PROC_MOJI_SEL_ANM;
        }
    } else if (stick->checkDownTrigger()) {
        mPrevColumn = mCharColumn;
        mPrevRow = mCharRow;
        MojiSelectAnmInit();
        mCharRow++;

        if (mCharRow > 4) {
            mCharRow = 4;
            mDoAud_seStart(Z2SE_SY_CURSOR_OPTION, 0, 0, 0);
            menuCursorPosSet();
            mSelProc = PROC_MOJI_SEL_ANM2;
        } else {
            mDoAud_seStart(Z2SE_SY_NAME_CURSOR, 0, 0, 0);
            mSelProc = PROC_MOJI_SEL_ANM;
        }
    }
}

