    if (!dComIfGs_isCollectMirror(0) && i_dComIfGs_isEventBit(0x5420)) {
        dComIfGs_onCollectMirror(0);
    }

    if (!dComIfGs_isCollectCrystal(3) && i_dComIfGs_isEventBit(0x5410)) {
        dComIfGs_onCollectCrystal(3);
    }

